function get_docRegularPage(){let markup;return'\n<h3>Issue. Booklet Regular Page.</h3>\n<p>Style the booklet regular page as in Fig.1.</p>\n<h5>Fig.1</h5>        \n<img src="\\assets\\picsDoc\\doc_docRegularPage.jpg" \nclass="img-responsive" width="256" height="440">\n<br>\n\n<h4>Code.</p>\n<h5><strong>Keywords: </strong></h5>\n<h5>1. CSS styling.</h5>\n\n<fieldset>\n<legend>CSS</legend>\n<div class="legend2">booklet.css</div>\n<pre><code class="language-css">\n/***********************************\n     regular page                          \n ***********************************/\n#regularPage {\n    min-width: 220px; \n    max-width: 100%;\n    background-color: #eee;    \n    overflow-x: hidden; \n    overflow-y: scroll;\n    direction: rtl; \n    text-align: left;\n    border: 2px solid  rgb(186, 190, 192); \n    border-radius:5% 91% 70% 12% / 1% 3% 3% 1%;\n    box-shadow: inset 25px 0px 25px -25px rgba(73, 73, 73, 0.45); \n}\n#regularPage .regular-page-header{\n    direction: ltr;\n}\n#regularPage .regular-page-content{\n    direction: ltr;     \n}\n#regularPage .regular-page-footer{\n    direction: ltr;\n}\n#regularPageCover {    \n    flex: 1 0 400px;    \n    min-width: 220px;    \n    max-width: 700px;    \n    background:#eee;\n    display: none;\n}\n#regularPageCover.visible {\n    display: block;\n}\n.regular-page-header {    \n    position: -webkit-sticky;\n    position: sticky;\n    top: 0;      \n    height: 3rem;\n    background-color: #eee; \n    z-index: 0;\n    box-shadow: inset 12px 0px 25px -25px rgba(73, 73, 73, 0.45);\n}\n.regular-page-footer {    \n    position: -webkit-sticky;\n    position: sticky;\n    bottom: 0;    \n    height: 2rem;\n    background-color: #eee; \n    z-index: 9999;\n    box-shadow: inset 12px 0px 25px -25px rgba(73, 73, 73, 0.45);    \n} \n/*    text formatting      */\n.regular-page {    \n    font-weight: normal;    \n}\n.regular-page p {\n    display: block; \n    max-width: 600px;    \n    word-wrap: break-word;    \n    margin-block-start: 1em;\n    margin-block-end: 1em;\n    margin-inline-start: 2em;\n    margin-inline-end: 1.0em;    \n    max-height: 700px; \n    color: #54585A;\n}\n.regular-page q {\n    display: block;   \n    max-width: 500px;    \n    word-wrap: break-word;    \n    margin-block-start: 1em;\n    margin-block-end: 1em;\n    margin-inline-start: 4.5em;\n    margin-inline-end: 1.0em;    \n    max-height: 700px;   \n}\n.regular-page p a {\n    /* color: dodgerblue; */\n    /* color:#00aff0; */\n    font-weight: 500;   \n}\n.regular-page h1,\n.regular-page h2,\n.regular-page h3,\n.regular-page h4,\n.regular-page h5,\n.regular-page h6 {\n    font-family: Georgia, serif;\n    font-weight: 600;    \n    margin: 0.6rem 0px 0.4rem 0px;\n    padding-left: 1.4rem;    \n}\n.regular-page p,\n.regular-page dd {\n    line-height: 1.4em; \n    margin: 0;\n    padding-left: 1.4rem;\n    padding-right: 0.4rem;    \n}\n.regular-page li {\n    line-height: 1.4em; \n    margin: 0;\n    padding-left: 0.2rem;\n    font-family: Georgia, Roboto, \'Roboto Condensed\', \'Bookman Old Style\', serif;\n    font-weight: 600; \n    font-size: 0.9rem !important;   \n} \n.regular-page ul {\n    margin-block-start: 0;\n    margin-block-end:0.4rem;\n    padding-inline-start: 2.5rem;\n}\n.regular-page a { \n    padding: 0.2rem 1rem 0 0.2rem;       \n    text-decoration: none;\n    font-family: Georgia, Roboto, \'Roboto Condensed\', \'Bookman Old Style\', serif;    \n    /* font-size: 0.9rem !important;  */\n    font-size: 1rem !important; \n    line-height: 1rem;\n    font-weight: 600; \n    color: #2b6dad;\n    border: none;\n    background: none;\n    width: 100%;\n    text-align: left;\n    cursor: pointer;\n    outline: none;\n}\n.regular-page a:hover {\n    text-decoration: underline ;   \n}\n</code></pre></fieldset>\n'}