class DropdownContainer{constructor(dropdownContainerType,dropdownContainerClasses){this.dropdownContainerType=dropdownContainerType,this.dropdownContainerClasses=dropdownContainerClasses,this.dropdownItems=[]}addDropdownItem(itemType,itemClass,itemOnClick,itemText,iconClass){const item=new Item_IconText(itemType,itemClass,itemOnClick,itemText,iconClass);return this.dropdownItems.push(item),this}toHtml(){return`<${this.dropdownContainerType} class = "${this.dropdownContainerClasses}">\n                   ${this.dropdownItems.map(i=>i.toHtml()).join("")}                      \n            </${this.dropdownContainerType}>`}}