class IconContainer{constructor(iconContainerType,iconContainerClasses,dataAttrib,tooltipText,onClick){this.iconContainerType=iconContainerType,this.iconContainerClasses=iconContainerClasses,this.dataAttrib=dataAttrib,this.tooltipText=tooltipText,this.onClick=onClick,this.icons=[],this.dropdowns=[]}addIcon(iconClasses){const icon=new Icon(iconClasses);return this.icons.push(icon),this}addDropdownContainer(dropdownContainerType,dropdownContainerClasses){const dropdownContainer=new DropdownContainer(dropdownContainerType,dropdownContainerClasses);return this.dropdowns.push(dropdownContainer),this.lastDropdownContainer=dropdownContainer,this}toHtml(){return`<${this.iconContainerType} class = "${this.iconContainerClasses}"\n                   ${this.dataAttrib} = "${this.tooltipText}" onClick="${this.onClick};">\n                       ${this.icons.map(i=>i.toHtml()).join("")} \n                       ${this.dropdowns.map(i=>i.toHtml()).join("")}                                          \n                </${this.iconContainerType}>`}}