class InputContainer{constructor(inputContainerType,inputContainerClasses,onClick,onClickSearch){this.inputContainerType=inputContainerType,this.inputContainerClasses=inputContainerClasses,this.onClick=onClick,this.iconContainers=[],this.inputs=[]}iconContainer(iconContainerType,iconContainerClasses,dataAttrib,tooltipText,onClick){const iconContainer=new IconContainer(iconContainerType,iconContainerClasses,dataAttrib,tooltipText,onClick);return this.iconContainers.push(iconContainer),this.lastIconContainer=iconContainer,this}addIcon(iconClasses){return this.lastIconContainer.addIcon(iconClasses),this}addInput(inputType,placeholder,inputClasses){const input=new Input(inputType,placeholder,inputClasses);return this.inputs.push(input),this}toHtml(){return`<${this.inputContainerType} class = "${this.inputContainerClasses}" onkeypress="${this.onClick};">          \n                    ${this.inputs.map(ip=>ip.toHtml()).join("")} \n                    ${this.iconContainers.map(ic=>ic.toHtml()).join("")}                                          \n                </${this.inputContainerType}}>`}}